// With my computer as my forge, I craft this code in dedication to the Lame One, whom is my patron and whom I love. 

package main;

public class Breaker {

	public static void main(String[] args) {
		String twoEncrypt = "";
		String threeEncrypt = "";

		System.out.println(breaker(twoEncrypt));
		System.out.println(breaker(threeEncrypt));
	}

	public static String breaker(String message) {
		String result = "";
		
		for(int i = 0; i < message.length(); i++) {
			if(i%5 == 0)
				result+=" ";
			result+=message.charAt(i);
		}
		
		return result;
	}
}
